/**
 * Calculate options for opening a new window.
 *
 * @param o input oject
 * @param o.w desired width of new window
 * @param o.h desired height og new window
 * @returns A string that can be passed as the 3rd argument to `window.open`
 */
declare const getOptions: ({ w, h }: {
    w: number;
    h: number;
}) => string;

/**
 * Opens a new window with the Privy popup options.
 *
 * @param o input object
 * @param o.location the url of the new window
 * @returns a reference to the opened window, or null if it failed to open
 */
declare const trigger: (o?: {
    location?: string;
}) => Window | null;

declare const VERSION = "__VERSION__";

export { VERSION, getOptions, trigger };
